CREATE TABLE db_comp(id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, user_id INT NOT NULL, slot INT NOT NULL, devil VARCHAR(4096) DEFAULT '');
CREATE TABLE db_storage(id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, user_id INT NOT NULL, slot INT NOT NULL, devil VARCHAR(4096) DEFAULT '');
CREATE TABLE users(id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, name VARCHAR(64) NOT NULL, email VARCHAR(256) NOT NULL, pass VARCHAR(40) NOT NULL, type INT NOT NULL, last_login TIMESTAMP, active BOOL DEFAULT true NOT NULL, admin BOOL DEFAULT false NOT NULL, view_db BOOL DEFAULT true NOT NULL, modify_db BOOL DEFAULT false NOT NULL, admin_db BOOL DEFAULT false NOT NULL);
